reports.query:
// Get gene count per Tax ID
MATCH (g:Gene)
WITH distinct(g.TAXID) as TAXID, count(g) as Gene_count
WITH collect({TAXID: TAXID, Gene_count: Gene_count, Protein_count: 0}) as rows
// Get protein count per Tax ID and join results with previous query
MATCH (p:Protein)
WITH distinct(p.TAXID) as TAXID, count(p) as Protein_count, rows
WITH rows + collect({TAXID: TAXID, Gene_count: 0, Protein_count: Protein_count}) as allRows
// separate the rows
UNWIND allRows as row
// Define the column variables from each row
WITH row.TAXID as TAXID, row.Gene_count as Gene_count, row.Protein_count as Protein_count
// Return the columns by adding the Gene-count and Protien_count to avoid repeated Tax IDs
RETURN TAXID, sum(Gene_count) as Gene_count, sum(Protein_count) as Protein_count
// order by Gene_count (to be at the top, as they are fewer than Protein_count)
ORDER BY Gene_count DESC
